import { Component } from 'react';
import Headering from '../Header/Headering';
import Hero from '../Hero/Hero';
import Price from '../Main/Price';
import Modaling from '../UI/Modaling/Modaling';


class Builder extends Component {
  constructor(props) {
    super()
    this.state = {
     obj: [
        {
          id: 1,
          name: 'Sushi',
          ingridient: 'Finest fish and veggies',
          price: 22.99,
          count: 0
        },
        {
          id: 2,
          name: 'Schnitel',
          ingridient: 'A german speciality',
          price: '16.50',
          count: 0
        },
        {
          id: 3,
          name: 'Barbecue Burger',
          ingridient: 'American, raw, meaty',
          price: 12.99,
          count: 0
        },
        {
          id: 4,
          name: 'Green Bowl',
          ingridient: 'Healthy...and green...y',
          price: 18.99,
          count: 0
        },
      ],

      newArray: [],
      modal: false
    }
  }
  render() {

    let Opening = () => {this.setState({modal: true})}

    let Closing = () => {this.setState({modal: false})}

    //Added Product
    let addProduct = (e) => {
      e.preventDefault()
      let idd = e.target.id
      let value = e.target[0].value
      let newArr = []
      let arr = []
      console.log(e)
      this.state.obj.map(item => {
        newArr.push(item)
      })

      newArr.map(item => {
        // let test = []
        if(+idd === item.id) {
          item.count = value
          arr.push(item)
        }
      })

      this.setState({newArray: [...this.state.newArray, ...arr]})
      this.setState({obj: [...newArr]})
    }


    //All Count
    let allCount = 0
    this.state.obj.map(item => {
      allCount += +item.count
    })
    console.log(allCount)

    return(
      <>
        <Headering
          opening={Opening}
          counts={allCount}
        />
        <Hero />
        <Price
          addProd={addProduct}
          obj={this.state.obj}
        />
        <Modaling
          opening={this.state.modal}
          closing={Closing}
          product={this.state.newArray}
        />
      </>
    )
  }
}
export default Builder;
